import React from 'react';
import { useState } from 'react';
import { useEffect } from 'react';
import Navbar from './Navbar';
import { Trash } from 'phosphor-react';
import Coupon from './Coupon';
import Footer from './Footer';
import Product from './Product';

const CartItem = ({ cartItem, handleDeleteCartItem }) => {
  const [quantity, setQuantity] = useState(1);
  // const Cart = () => {
  //   const [cartItems, setCartItems] = useState(null);
  //   const [quantity, setQuantity] = useState(1);

  //add an event handler for the onChange event input field with a fallback value that sets the quantity to 1
  const handleQuantityChange = (event) => {
    setQuantity(parseInt(event.target.value) || 1);
  };

  function handleKeyDown(event) {
    if (event.key === '-' && quantity > 1) {
      setQuantity(quantity - 1);
    }
  }
  // const handleDeleteCartItem = (cartItem) => {
  //   let filteredCartItems = cartItems.filter((item) => item.id !== cartItem.id);

  //   setCartItems(filteredCartItems);
  //   localStorage.setItem('cartItems', JSON.stringify(filteredCartItems));
  //   console.log(filteredCartItems);
  // };

  console.log(cartItems);

  useEffect(() => {
    // Compute the total price by multiplying quantity and price
    const totalPrice = quantity * cartItem.price;
    setTotalPrice(totalPrice);
    // update the quantity for this cart item in local storage
    const updatedCartItems = JSON.parse(localStorage.getItem('cartItems')).map(
      (item) => {
        if (item.id === cartItem.id) {
          return {
            ...item,
            quantity,
          };
        } else {
          return item;
        }
      }
    );
    localStorage.setItem('cartItems', JSON.stringify(updatedCartItems));
  }, [quantity, cartItem]);
  //get Cart Items from local storage
  // const carts = localStorage.getItem('cartItems');
  //if carts has a valid value or an array then update the cart items state with this carts array
  //   if (carts) {
  //     setCartItems(JSON.parse(carts));
  //   }
  //   return () => {
  //     //
  //   };
  // }, []);
  return (
    <>
      <Navbar />

      <div className="wrapper text-white">
        <h2 className="shopping">Shopping Cart</h2>
        <section id="cart-container" className="container">
          <table width="100%">
            <thead>
              <tr>
                <td>Remove</td>
                <td>Image</td>
                <td>Product</td>
                <td>Price</td>
                <td>Quantity</td>
                <td>Total</td>
              </tr>
            </thead>
          </table>
        </section>

        {cartItems === null ||
          (cartItems.length === 0 && <h1>Your cart is empty!</h1>)}
        {cartItems &&
          cartItems.length > 0 &&
          cartItems.map((cartItem, index) => {
            return (
              <>
                <section id="cart-container" className="container">
                  <table width="100%">
                    <tbody>
                      <tr className="border">
                        <td>
                          <button
                            className="trash-can"
                            onClick={() => handleDeleteCartItem(cartItem)}
                          >
                            <Trash size={24} />
                          </button>
                        </td>
                        <td className="image">
                          <img
                            className="cartImage"
                            src={cartItem.api_featured_image}
                            alt={cartItem.name}
                          />
                        </td>
                        <td className>
                          <h3>{cartItem.name}</h3>
                        </td>
                        <td>
                          <p>{cartItem.price_sign + cartItem.price}</p>
                        </td>
                        <td>
                          <input
                            className="quantity"
                            onChange={handleQuantityChange}
                            onKeyDown={handleKeyDown}
                            value={quantity}
                            type="number"
                          ></input>
                        </td>
                        <td>
                          <h5>{totalPrice}</h5>
                        </td>
                      </tr>
                    </tbody>
                  </table>
                </section>
              </>

              // {/* <p>{cartItem.description}</p> */}
            );
          })}
      </div>
      <Coupon />
      <Footer />
    </>
  );
};

export default Cart;
