import React, { useState, useEffect } from 'react';
import { Trash } from 'phosphor-react';
import Coupon from './Coupon';
import Footer from './Footer';
import Navbar from './Navbar';
import Product from './Product';

const Cart = () => {
  const [cartItems, setCartItems] = useState(null);
  // const [quantity, setQuantity] = useState(1);
  const [totalPrice, setTotalPrice] = useState(0);
  const [totalQuantity, setTotalQuantity] = useState();
  const [cartItemTotalPrice, setCartItemTotalPrice] = useState({});
  //add an event handler for the onChange event input field with a fallback value that sets the quantity to 1

  const handleQuantityChange = (event, itemId) => {
    const newQuantity = parseInt(event.target.value) || 1;
    const updatedCartItems = cartItems.map((item) => {
      if (item.id === itemId) {
        const newTotalPrice = newQuantity * item.price;
        setCartItemTotalPrice({
          ...cartItemTotalPrice,
          [itemId]: newTotalPrice,
        });
        return {
          ...item,
          quantity: newQuantity,
        };
      }
      return item;
    });
    setCartItems(updatedCartItems);
  };

  function handleKeyDown(event) {
    if (event.key === '-' && quantity > 1) {
      setQuantity(quantity - 1);
    }
  }
  const handleDeleteCartItem = (cartItem) => {
    let filteredCartItems = cartItems.filter((item) => item.id !== cartItem.id);

    setCartItems(filteredCartItems);
    localStorage.setItem('cartItems', JSON.stringify(filteredCartItems));
    console.log(filteredCartItems);
  };

  console.log(cartItems);

  useEffect(() => {
    //get Cart Items from local storage
    const carts = localStorage.getItem('cartItems');

    //if carts has a valid value or an array then update the cart items state with this carts array
    if (carts) {
      const parsedCarts = JSON.parse(carts);
      const cartItemsWithQuantity = parsedCarts.map((item) => ({
        ...item,
        quantity: 1, // add quantity property with default value
      }));
      setCartItems(cartItemsWithQuantity);
      setCartItemTotalPrice(
        cartItemsWithQuantity.reduce(
          (acc, item) => ({ ...acc, [item.id]: item.price }),
          {}
        )
      );
    } else {
      setCartItems(null);
      setCartItemTotalPrice({});
    }
  }, []);

  useEffect(() => {
    if (cartItems) {
      const totalPrice = cartItems.reduce((acc, item) => {
        return acc + item.quantity * item.price;
      }, 0);
      setTotalPrice(totalPrice);
      const totalQuantity = cartItems.reduce((acc, item) => {
        return acc + item.quantity;
      }, 0);
      setTotalQuantity(totalQuantity);
    }
  }, [cartItemTotalPrice]);
  const grandTotal = totalPrice;
  return (
    <>
      <Navbar />

      <div className="wrapper text-white">
        <h2 className="shopping">Shopping Cart</h2>
        <section id="cart-container" className="container">
          <table width="100%">
            <thead>
              <tr>
                <td>Remove</td>
                <td>Image</td>
                <td>Product</td>
                <td>Price</td>
                <td>Quantity</td>
                <td>Total</td>
              </tr>
            </thead>
          </table>
        </section>

        {cartItems === null ||
          (cartItems.length === 0 && <h1>Your cart is empty!</h1>)}
        {cartItems &&
          cartItems.length > 0 &&
          cartItems.map((cartItem, index) => {
            return (
              <>
                <section id="cart-container" className="container">
                  <table width="100%">
                    <tbody>
                      <tr className="border">
                        <td>
                          <button
                            className="trash-can"
                            onClick={() => handleDeleteCartItem(cartItem)}
                          >
                            <Trash size={24} />
                          </button>
                        </td>
                        <td className="image">
                          <img
                            className="cartImage"
                            src={cartItem.api_featured_image}
                            alt={cartItem.name}
                          />
                        </td>
                        <td className>
                          <h3>{cartItem.name}</h3>
                        </td>
                        <td>
                          <p>{cartItem.price_sign + cartItem.price}</p>
                        </td>
                        <td>
                          <input
                            className="quantity"
                            onChange={(event) =>
                              handleQuantityChange(event, cartItem.id)
                            }
                            onKeyDown={handleKeyDown}
                            value={cartItem.quantity}
                            type="number"
                          ></input>
                        </td>
                        <td>
                          <h5>${cartItemTotalPrice[cartItem.id]}</h5>
                        </td>
                      </tr>
                    </tbody>
                  </table>
                </section>
              </>
            );
          })}
      </div>
      {/* <Coupon onCouponSubmit={handleCouponSubmit} /> */}
      <Coupon />
      <Footer />
    </>
  );
};

export default Cart;
