import React from 'react';
import { useState } from 'react';
import { useEffect } from 'react';
import Navbar from './Navbar';
import { Trash } from 'phosphor-react';
import Coupon from './Coupon';
import Footer from './Footer';
import Product from './Product';

const Cart = () => {
  const [cartItem, setCartItem] = useState([]);
  // const [cartItems, setCartItems] = useState(null);
  const [quantity, setQuantity] = useState(1);
  const [cartQuantities, setCartQuantities] = useState({});
  const calculateTotalPrice = () => {
    let total = 0;
    cartItems.forEach((item) => {
      total += item.quantity * item.price;
    });
    return total;
  };
  //add an event handler for the onChange event input field with a fallback value that sets the quantity to 1
  const handleQuantityChange = (event) => {
    setQuantity(parseInt(event.target.value) || 1);
  };

  function handleKeyDown(event) {
    if (event.key === '-' && quantity > 1) {
      setQuantity(quantity - 1);
    }
  }
  const handle = (e, cartItem, quantity) => {
    const { name, value } = e.target;
    setCartQuantities((prev) => ({
      ...prev,
      [cartItem.id]: value,
    }));
  };

  const handleDeleteCartItem = (cartItem) => {
    let filteredCartItems = cartItems.filter((item) => item.id !== cartItem.id);
    setCartItems(filteredCartItems);
    localStorage.setItem('cartItems', JSON.stringify(filteredCartItems));
    console.log(filteredCartItems);
  };
  const updateQuantity = (itemToUpdate, newQuantity) => {
    const updatedCartItems = cartItems.map((item) => {
      if (item === itemToUpdate) {
        return { ...item, quantity: newQuantity };
      }
      return item;
    });
    setCartItems(updatedCartItems);
  };

  console.log(cartItems);

  useEffect(() => {
    //get Cart Items from local storage
    const carts = localStorage.getItem('cartItems');
    //if carts has a valid value or an array then update the cart items state with this carts array
    if (carts) {
      setCartItems(JSON.parse(carts));
    }
    //get Cart Quantities from local storage
    const cartQuantities = JSON.parse(localStorage.getItem('cartQuantities'));
    //if cartQuantities has a valid value or an array then update the cart quantities state with this cartQuantities array
    if (cartQuantities) {
      setCartQuantities(cartQuantities);
    }
    return () => {
      //
    };
  }, []);
  const [total, setTotalQuantity] = useState(0);

  useEffect(() => {
    if (cartItem) {
      setTotalQuantity(
        parseInt(cartItem.price_sign + cartItem.price) * cartQuantities
      );
    }
  }, [cartItem]);
  return (
    <>
      <Navbar />

      <div className="wrapper text-white">
        <h2 className="shopping">Shopping Cart</h2>
        <section id="cart-container" className="container">
          <table width="100%">
            <thead>
              <tr>
                <td>Remove</td>
                <td>Image</td>
                <td>Product</td>
                <td>Price</td>
                <td>Quantity</td>
                <td>Total</td>
              </tr>
            </thead>
          </table>
        </section>

        {cartItems === null ||
          (cartItems.length === 0 && <h1>Your cart is empty!</h1>)}
        {cartItems &&
          cartItems.length > 0 &&
          cartItems.map((cartItem, index) => {
            return (
              <>
                <section id="cart-container" className="container">
                  <table width="100%">
                    <tbody>
                      <tr className="border">
                        <td>
                          <button
                            className="trash-can"
                            onClick={() => handleDeleteCartItem(cartItem)}
                          >
                            <Trash size={24} />
                          </button>
                        </td>
                        <td className="image">
                          <img
                            className="cartImage"
                            src={cartItem.api_featured_image}
                            alt={cartItem.name}
                          />
                        </td>
                        <td className>
                          <h3>{cartItem.name}</h3>
                        </td>
                        <td>
                          <p>{cartItem.price_sign + cartItem.price}</p>
                        </td>
                        <td>
                          <input
                            className="quantity"
                            onChange={(event) =>
                              handleQuantityChange(event, index)
                            }
                            onKeyDown={(event) => handle}
                            value={quantity}
                            type="number"
                          ></input>
                        </td>
                        <td>
                          <h5>${calculateTotalPrice()}</h5>
                        </td>
                      </tr>
                    </tbody>
                  </table>
                </section>
              </>
            );
          })}
      </div>
      <Coupon />
      <Footer />
    </>
  );
};

export default Cart;
