import React from 'react';
import { useState } from 'react';
import { useEffect } from 'react';
import Navbar from './Navbar';
import Product from './Product';

const Cart = () => {
  const [cartItems, setCartItems] = useState(null);
  const handleDeleteCartItem = (cartItem) => {
    let filteredCartItems = cartItems.filter((item) => item.id !== cartItem.id);
    console.log(filteredCartItems);
  };

  console.log(cartItems);

  useEffect(() => {
    const carts = localStorage.getItem('cartItems');
    if (carts) {
      setCartItems(JSON.parse(carts));
    }
    return () => {
      //
    };
  }, []);
  return (
    <>
      <Navbar />
      <div className="wrapper text-white">
        {!cartItems && <h1>Your cart is empty!</h1>}
        {cartItems &&
          cartItems.length > 0 &&
          cartItems.map((cartItem, index) => {
            return (
              <div>
                <img src={cartItem.api_featured_image} alt={cartItem.name} />
                <h2>{cartItem.name}</h2>
                <p>{cartItem.description}</p>
                <p>{cartItem.price_sign + cartItem.price}</p>
                <button onClick={() => handleDeleteCartItem(cartItem)}>
                  Delete
                </button>
              </div>
            );
          })}
      </div>
    </>
  );
};

export default Cart;
